/**
 * @file 用于应用最初的启动组件
 * @author Liang
 */

import React from 'react'
import {hashHistory, Router} from 'react-router'
import {Provider} from 'mobx-react'

export default routes => {
    class MainApp extends React.Component {
        render() {
            const routerProps = {
                history: hashHistory,
                routes
            }
            return <Provider {...this.props.context}>
                <Router {...routerProps}/>
            </Provider>
        }
    }

    return MainApp
}
