export const routes = {
    path: '/',
    indexRoute: {onEnter: (nextState, replace) => replace('entry-list')},
    childRoutes: [
        {
            path: 'entry-list',
            getComponent(location, callback) {
                require.ensure([], () => {
                    let Page = require('./Page').default;
                    callback(null, Page);
                });
            }
        }
    ]
};

